const { default: http } = require("./index");

async function main() {
  const [res, err] = await http.get(
    "https://www.google.com/",
    {},
    {
      retry: 3,
      retryDelay: 3000,
      interval: 3000,
      requestUntil: (res) => true,
    }
  );
  if (res) {
    console.log(res.status);
  }
  //   console.log(err);
}

main();
